import React, { useEffect, useState } from 'react';
import { classifyCourses } from '../services/courseService';


const Courses = () => {
    const [coreCourses, setCoreCourses] = useState([]);
    const [electiveCourses, setElectiveCourses] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadCourses = async () => {
            try {
                const core = await fetchCoreCourses();
                const electives = await fetchElectiveCourses();
                setCoreCourses(core);
                setElectiveCourses(electives);
            } catch (error) {
                console.error('Error loading courses:', error.message);
            } finally {
                setLoading(false);
            }
        };

        loadCourses();
    }, []);

    if (loading) return <div>Loading courses...</div>;

    return (
        <div>
            <h2>Core Courses</h2>
            <ul>
                {coreCourses.map((course) => (
                    <li key={course.id}>{course.name} - {course.credits} Credits</li>
                ))}
            </ul>
            <h2>Elective Courses</h2>
            <ul>
                {electiveCourses.map((course) => (
                    <li key={course.id}>{course.name} - {course.credits} Credits</li>
                ))}
            </ul>
        </div>
    );
};

export default Courses;
