// App.js
import React, { useState } from 'react';
import CoursePlannerForm from './components/CoursePlannerForm';
import CourseSchedule from './components/CourseSchedule';
import { generateSchedule } from './services/courseService';

const App = () => {
  const [generatedSchedule, setGeneratedSchedule] = useState([]);

  // Function to handle schedule generation
  const onGenerateSchedule = async (careerAspiration, creditsPerQuarter) => {
    try {
      const schedule = await generateSchedule(careerAspiration, creditsPerQuarter);
      setGeneratedSchedule(schedule.schedule);
    } catch (error) {
      console.error('Failed to generate schedule', error);
      alert('Failed to generate schedule. Please try again later.');
    }
  };

  return (
    <div className="App">
      <h1>Auto Course Planner</h1>
      <CoursePlannerForm onGenerateSchedule={onGenerateSchedule} />
      {/* Display the generated schedule here */}
      <CourseSchedule schedule={generatedSchedule} />
    </div>
  );
};

export default App;
