import React from 'react';
import './CourseSchedule.css';

const CourseSchedule = ({ schedule }) => {
  if (!schedule || schedule.length === 0) {
    return <div>No schedule generated yet.</div>;
  }

  return (
    <div className="schedule-container">
      <h2>Academic Year 2023-2024</h2>
      <div className="schedule-grid">
        {schedule.map((quarter, index) => (
          <div key={index} className="quarter-card">
            <div className="quarter-header">{quarter.quarter}</div>
            <div className="courses-list">
              {quarter.courses.map((course) => (
                <div key={course.id} className="course-card">
                  <div className="course-title">
                    <span className="course-id">{course.id}</span> - {course.name}
                  </div>
                  <div className="course-credits">{course.credits} CR</div>
                </div>
              ))}
            </div>
            <div className="credits-total">
              Credits: {quarter.courses.reduce((total, course) => total + course.credits, 0)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseSchedule;
