import React, { useState } from 'react';

const CoursePlannerForm = ({ onGenerateSchedule }) => {
  const [careerAspiration, setCareerAspiration] = useState('');
  const [creditsPerQuarter, setCreditsPerQuarter] = useState(15);
  const [coursePreference, setCoursePreference] = useState('');
  const [transcriptFile, setTranscriptFile] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate that all fields are filled
    if (!careerAspiration || !coursePreference) {
      alert('Please fill all required fields.');
      return;
    }

    // Prepare form data to be sent
    const formData = new FormData();
    formData.append('careerAspiration', careerAspiration);
    formData.append('creditsPerQuarter', creditsPerQuarter);
    formData.append('coursePreference', coursePreference);
    if (transcriptFile) {
      formData.append('transcript', transcriptFile);
    }

    onGenerateSchedule(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ marginBottom: '1em' }}>
        <label htmlFor="careerAspiration">
          Career Aspiration:
          <select 
            id="careerAspiration" 
            name="careerAspiration"
            value={careerAspiration} 
            onChange={(e) => setCareerAspiration(e.target.value)}
            required
          >
            <option value="">Select Career Focus</option>
            <option value="AI and Machine Learning">AI and Machine Learning</option>
            <option value="Game Development">Game Development</option>
            <option value="Cybersecurity">Cybersecurity</option>
          </select>
        </label>
      </div>

      <div style={{ marginBottom: '1em' }}>
        <label htmlFor="creditsPerQuarter">
          Credits Per Quarter:
          <input
            id="creditsPerQuarter"
            name="creditsPerQuarter"
            type="number"
            min="15"
            max="18"
            value={creditsPerQuarter}
            onChange={(e) => setCreditsPerQuarter(e.target.value)}
            required
          />
        </label>
      </div>

      <div style={{ marginBottom: '1em' }}>
        <label htmlFor="coursePreference">
          Course Preference:
          <select 
            id="coursePreference"
            name="coursePreference"
            value={coursePreference} 
            onChange={(e) => setCoursePreference(e.target.value)}
            required
          >
            <option value="">Select Course Preference</option>
            <option value="all-core">All Core Courses</option>
            <option value="all-elective">All Elective Courses</option>
            <option value="2-core-1-elective">2 Core Courses, 1 Elective</option>
            <option value="2-elective-1-core">2 Elective Courses, 1 Core</option>
          </select>
        </label>
      </div>

      <div style={{ marginBottom: '1em' }}>
        <label htmlFor="transcript">
          Upload Unofficial Transcript (PDF):
          <input
            id="transcript"
            name="transcript"
            type="file"
            accept=".pdf"
            onChange={(e) => setTranscriptFile(e.target.files[0])}
          />
        </label>
      </div>

      <button type="submit">Generate Schedule</button>
    </form>
  );
};

export default CoursePlannerForm;
