import React, { useEffect, useState } from 'react';
import { fetchCourses, fetchCompletedCourses } from '../services/courseService'; // Ensure both functions are properly implemented in your backend

const Courses = () => {
    const [coreCourses, setCoreCourses] = useState([]);
    const [electiveCourses, setElectiveCourses] = useState([]);
    const [completedCourses, setCompletedCourses] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadCourses = async () => {
            try {
                // Fetch classified courses (core and electives)
                const { core, electives } = await fetchCourses();
                setCoreCourses(core);
                setElectiveCourses(electives);

                // Fetch completed courses
                const completed = await fetchCompletedCourses();
                setCompletedCourses(completed);
            } catch (error) {
                console.error('Error loading courses:', error.message);
            } finally {
                setLoading(false);
            }
        };

        loadCourses();
    }, []);

    if (loading) return <div>Loading courses...</div>;

    return (
        <div>
            <h2>Completed Courses</h2>
            <ul>
                {completedCourses.length > 0 ? (
                    completedCourses.map((course, index) => (
                        <li key={index}>
                            {course.CourseTitleLong || `CSS ${course}`} - {course.Credits || 'N/A'} Credits
                        </li>
                    ))
                ) : (
                    <p>No completed courses found.</p>
                )}
            </ul>

            <h2>Core Courses</h2>
            <ul>
                {coreCourses.map((course) => (
                    <li key={course.CourseNumber}>
                        {course.CourseTitleLong} - {course.Credits} Credits
                    </li>
                ))}
            </ul>

            <h2>Elective Courses</h2>
            <ul>
                {electiveCourses.map((course) => (
                    <li key={course.CourseNumber}>
                        {course.CourseTitleLong} - {course.Credits} Credits
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Courses;
