// App.js
import React, { useState } from 'react';
import CoursePlannerForm from './components/CoursePlannerForm';
import CourseSchedule from './components/CourseSchedule';
import { generateSchedule } from './services/courseService';

const App = () => {
  const [schedule, setSchedule] = useState([]);
  const [completedCourses, setCompletedCourses] = useState([]);

  const handleGenerateSchedule = async (careerAspiration, creditsPerQuarter, coursePreference, transcript) => {
    try {
      const response = await generateSchedule(careerAspiration, creditsPerQuarter, coursePreference, transcript);
      setSchedule(response.schedule);
      setCompletedCourses(response.completedCourses);
    } catch (error) {
      console.error('Failed to generate schedule', error);
    }
  };

  return (
    <div>
      <h1>Auto Course Planner</h1>
      <CoursePlannerForm onGenerateSchedule={handleGenerateSchedule} />
      <CourseSchedule schedule={schedule} />
      {completedCourses.length > 0 && (
        <div>
          <h2>Completed Courses</h2>
          <ul>
            {completedCourses.map((course, index) => (
              <li key={index}>{course}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
