import React, { useState } from 'react';
import axios from 'axios';

const CoursePlannerForm = ({ setGeneratedSchedule }) => {
  const [careerAspiration, setCareerAspiration] = useState('');
  const [creditsPerQuarter, setCreditsPerQuarter] = useState(15);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Sending request to the backend to generate schedule
      const response = await axios.post('http://localhost:5001/api/generateSchedule', {
        careerAspiration,
        creditsPerQuarter,
      });

      // Setting the generated schedule in the parent component
      setGeneratedSchedule(response.data.schedule);
    } catch (error) {
      console.error('Failed to generate schedule:', error);
      alert('Failed to generate schedule. Please try again later.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Career Aspiration:
        <select value={careerAspiration} onChange={(e) => setCareerAspiration(e.target.value)}>
          <option value="">Select Career Focus</option>
          <option value="AI and Machine Learning">AI and Machine Learning</option>
          <option value="Game Development">Game Development</option>
          <option value="Software Development">Software Development</option>
          {/* Add more options as needed */}
        </select>
      </label>
      <label>
        Credits Per Quarter:
        <input
          type="number"
          min="15"
          max="18"
          value={creditsPerQuarter}
          onChange={(e) => setCreditsPerQuarter(e.target.value)}
        />
      </label>
      <button type="submit">Generate Schedule</button>
    </form>
  );
};

export default CoursePlannerForm;
