// frontend/src/services/courseService.js
import axios from 'axios';

const API_URL = 'http://localhost:5001/api';

export const getCoreCourses = async () => {
    try {
        const response = await axios.get(`${API_URL}/core_courses`);
        return response.data;
    } catch (error) {
        console.error('Error fetching core courses:', error.response ? error.response.data : error.message);
        throw error;
    }
};

export const getElectives = async (careerAspiration) => {
    try {
        const response = await axios.get(`${API_URL}/electives`, { params: { careerAspiration } });
        return response.data;
    } catch (error) {
        console.error('Error fetching electives:', error.response ? error.response.data : error.message);
        throw error;
    }
};
