import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Courses = () => {
    const [coreCourses, setCoreCourses] = useState([]);
    const [electiveCourses, setElectiveCourses] = useState([]);
    const [completedCourses, setCompletedCourses] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadCourses = async () => {
            try {
                // Fetch completed courses
                const { data: completedData } = await axios.get('/api/completed-courses');
                setCompletedCourses(completedData.completedCourses);

                // Fetch core and elective courses
                const { data: courseData } = await axios.get('/api/courses'); // Adjust API as per your backend
                setCoreCourses(courseData.core);
                setElectiveCourses(courseData.electives);
            } catch (error) {
                console.error('Error loading courses:', error.message);
            } finally {
                setLoading(false);
            }
        };

        loadCourses();
    }, []);

    if (loading) return <div>Loading courses...</div>;

    return (
        <div>
            <h2>Completed Courses</h2>
            <ul>
                {completedCourses.length > 0 ? (
                    completedCourses.map((course, index) => (
                        <li key={index}>CSS {course}</li>
                    ))
                ) : (
                    <p>No completed courses found. Upload a transcript on the Schedule Generator page.</p>
                )}
            </ul>

            <h2>Core Courses</h2>
            <ul>
                {coreCourses.map((course) => (
                    <li key={course.CourseNumber}>
                        {course.CourseTitleLong} - {course.Credits} Credits
                    </li>
                ))}
            </ul>

            <h2>Elective Courses</h2>
            <ul>
                {electiveCourses.map((course) => (
                    <li key={course.CourseNumber}>
                        {course.CourseTitleLong} - {course.Credits} Credits
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Courses;
