import React, { useState } from 'react';

const CoursePlannerForm = ({ onGenerateSchedule }) => {
  const [careerAspiration, setCareerAspiration] = useState('');
  const [creditsPerQuarter, setCreditsPerQuarter] = useState(15);
  const [coursePreference, setCoursePreference] = useState('all-core');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Now passing the additional 'coursePreference' value to the schedule generation function
    onGenerateSchedule(careerAspiration, creditsPerQuarter, coursePreference);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Career Aspiration:
        <select value={careerAspiration} onChange={(e) => setCareerAspiration(e.target.value)}>
          <option value="">Select Career Focus</option>
          <option value="AI and Machine Learning">AI and Machine Learning</option>
          <option value="Game Development">Game Development</option>
          <option value="Cybersecurity">Cybersecurity</option>
          {/* Add more options as needed */}
        </select>
      </label>
      <label>
        Credits Per Quarter:
        <input
          type="number"
          min="15"
          max="18"
          value={creditsPerQuarter}
          onChange={(e) => setCreditsPerQuarter(e.target.value)}
        />
      </label>
      <label>
        Course Preference:
        <select value={coursePreference} onChange={(e) => setCoursePreference(e.target.value)}>
          <option value="all-core">All Core Courses</option>
          <option value="all-elective">All Elective Courses</option>
          <option value="2-core-1-elective">2 Core Courses, 1 Elective</option>
          <option value="2-elective-1-core">2 Electives, 1 Core Course</option>
        </select>
      </label>
      <button type="submit">Generate Schedule</button>
    </form>
  );
};

export default CoursePlannerForm;
