// services/courseService.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5001/api';

// Function to fetch core courses
export const getCoreCourses = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/core_courses`);
    return response.data;
  } catch (error) {
    console.error('Failed to fetch core courses:', error);
    throw error;
  }
};

// Function to fetch electives based on career aspiration
export const getElectives = async (careerAspiration) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/electives`, {
      params: { careerAspiration },
    });
    return response.data;
  } catch (error) {
    console.error('Failed to fetch electives:', error);
    throw error;
  }
};

// Function to generate a course schedule based on career aspiration and credits per quarter
export const generateSchedule = async (careerAspiration, creditsPerQuarter) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/generateSchedule`, {
      careerAspiration,
      creditsPerQuarter,
    });
    return response.data;
  } catch (error) {
    console.error('Failed to generate schedule:', error);
    throw error;
  }
};
